import React, { useState } from "react";
import axios from "axios";

const ImageForm = () => {
  const [file, setFile] = useState();
  const [title, setTitle] = useState("");
  const [uploadPercentage, setUploadPercentage] = useState(0);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    setLoading(true);

    const formData = new FormData();
    formData.append("file", file);
    formData.append("title", title);

    const res = await axios.post("/api/images/upload", formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
      onUploadProgress: (progressEvent) => {
        const { loaded, total } = progressEvent;

        let percent = parseInt((loaded * 100) / total);
        setUploadPercentage(percent);
      },
    });

    setLoading(false);
    setUploadPercentage(0);

    console.log(res);
  };

  return (
    <div className="col-md-4 offset-md-4">
      {loading && (
        <div className="progress rounded-0">
          <div
            className="progress-bar progress-bar-striped bg-success"
            role="progressbar"
            style={{ width: `${uploadPercentage}%` }}
          >
            {uploadPercentage}%
          </div>
        </div>
      )}

      <div className="card bg-dark text-light rounded-0 p-4">
        <div className="card-body">
          <h1 className="h3 card-title">Subir Imagen</h1>
          <form onSubmit={handleSubmit}>
            {/* Upload Input */}
            <input
              type="text"
              className="form-control bg-dark text-light my-3"
              placeholder="Escribe un tï¿½tulo para tu foto"
              onChange={(e) => setTitle(e.target.value)}
            />
            <input
              type="file"
              className="form-control bg-dark text-light rounded-0 border border-secondary"
              onChange={handleChange}
            />
            <div className="my-3">
              <button
                className="btn btn-success rounded-0 w-100"
                disabled={loading || !file}
              >
                {!loading ? (
                  "Upload"
                ) : (
                  <span
                    className="spinner-border spinner-border-sm"
                    role="status"
                    aria-hidden="true"
                  ></span>
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ImageForm;
